# “The input string does not contain leading or trailing spaces and the words# are always separated by a single space.”# Could you do it in-place without allocating extra space?class WordReverserTwo  def reverse_words(string)    string = string.reverse    require 'pry'; binding.pry    string.length.times do |i|      if finish = nil        start = i        finish = i + 1      end    end  endendrequire 'rspec'RSpec.describe WordReverserTwo do  it 'can reverse words in place without extra space' do    wrt = WordReverserTwo.new    require 'pry'; binding.pry    answer = wrt.reverse_words("the  sky is blue")    expect(answer).to eq("blue is sky the")  endendputs "hello"